function onHotKey(id, keys)
	local sKeys = tostring(table.concat(keys, " "))
	for k, v in pairs(tBindList) do
		if sKeys == tostring(table.concat(v.v, " ")) then
			if k == 7 then 
				reconnect()
				return
			elseif k == 13 then 
				mainmenu()
				return
			end
		end
	end

