	if script == thisScript() then
		showCursor(false)
		 if autorelog.v and not quitGame and doesFileExist('moonloader\\AutoRebootControlSAMP.lua') then 
            AutoReboot.crash()
        end
		if priceab.v then 
		for i, marker in pairs(car_markers) do
			removeUser3dMarker(marker)
			car_markers[i] = nil
					end
				end
			end
		end

