							wait(100)
							stroboscopes(7086336, ptr, 2, 0, 0, 0)
							stroboscopes(7086336, ptr, 2, 0, 1, 1)
							wait(100)
							stroboscopes(7086336, ptr, 2, 0, 0, 1)
							stroboscopes(7086336, ptr, 2, 0, 1, 0)
							wait(100)
							stroboscopes(7086336, ptr, 2, 0, 1, 0)
							stroboscopes(7086336, ptr, 2, 0, 1, 1)
