function sampGetVersion()
	local ver = getModuleHandle("samp.dll")
	if ( ver == 0x0 ) then return false end
	local cmp =  memory.tohex( ver + 0xBABE, 10, true )
	if ( cmp == "F8036A004050518D4C24" ) then
		version = "0.3.7-R1"
    end
    if ( cmp == "E86D9A0A0083C41C85C0" ) then
        version = "0.3.7-R3"
    end
    return version
end

